import React, { useState } from "react";
import {
  NavbarContainer,
  StyledLogo,
  OptionsContainer,
  Option,
  OptionsContainerLogin,
  OptionLogin,
  VerticalBar,
  HamburgerMenu,
  DropdownMenu,
  MenuItem,
} from "../styles/NavbarStyles";
import LogoSvg from "../assets/steamLogo.svg"; // Ajusta la ruta según sea necesario

function Navbar() {
  const [selectedOption, setSelectedOption] = useState("TIENDA"); // Estado para la opción seleccionada
  const [isMenuOpen, setIsMenuOpen] = useState(false); // Estado para el menú hamburguesa

  const handleOptionClick = (option) => {
    setSelectedOption(option); // Cambiar la opción seleccionada al hacer clic
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen); // Alternar el estado del menú
  };

  return (
    <>
      <NavbarContainer>
        <StyledLogo src={LogoSvg} alt="Logo de Steam" />
        <HamburgerMenu onClick={toggleMenu}>
          {/* Icono de menú hamburguesa */}☰
        </HamburgerMenu>
        <OptionsContainer>
          {["TIENDA", "COMUNIDAD", "ACERCA DE", "SOPORTE"].map((option) => (
            <Option
              key={option}
              onClick={() => handleOptionClick(option)}
              isSelected={selectedOption === option} // Verifica si la opción está seleccionada
            >
              {option}
            </Option>
          ))}
        </OptionsContainer>
        <OptionsContainerLogin>
          <OptionLogin>Inicio de sesión</OptionLogin>
          <VerticalBar />
          <OptionLogin>Registrarse</OptionLogin>
        </OptionsContainerLogin>
        {isMenuOpen && (
          <DropdownMenu>
            {["TIENDA", "COMUNIDAD", "ACERCA DE", "SOPORTE"].map((option) => (
              <MenuItem key={option} onClick={() => handleOptionClick(option)}>
                {option}
              </MenuItem>
            ))}
            <MenuItem>Inicio de sesión</MenuItem>
            <MenuItem>Registrarse</MenuItem>
          </DropdownMenu>
        )}
      </NavbarContainer>
    </>
  );
}

export default Navbar;
