sourcesContent:
import styled, { keyframes } from "styled-components";

// Definimos la animación de apertura
const slideDown = keyframes`
  0% {
    opacity: 0;
    transform: translateY(-20%);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
`;

export const NavbarContainer = styled.nav`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: #181d24;
  padding: 30px 279px;
  color: #ffffff;
  position: relative;

  @media (max-width: 1342px) {
    padding: 30px 20px;
  }
`;

export const StyledLogo = styled.img`
  width: 176px;
  height: auto;

  @media (max-width: 1323px) {
    width: 150px;
    margin: 0 auto;
  }
`;

// Opciones del menú principal (desktop)
export const OptionsContainer = styled.div`
  margin-top: 5px;
  margin-left: 2.6%;
  display: flex;
  margin-right: auto;

  @media (max-width: 1323px) {
    display: none;
  }
`;

// Botón del menú móvil
export const MobileMenuButton = styled.button`
  display: none;
  @media (max-width: 1323px) {
    display: block;
    background: none;
    border: none;
    color: #ffffff;
    font-size: 24px;
    cursor: pointer;
  }
`;

// Menú móvil con animación
export const MobileMenu = styled.div`
  text-transform: uppercase;
  display: ${(props) => (props.isOpen ? "flex" : "none")};
  margin-top: 25px;
  flex-direction: column;
  background-color: #2e3641;
  width: 100%;
  position: absolute;
  top: 100%;
  left: 0;
  padding: 10px 0;
  z-index: 1;

  // Aplicar la animación de deslizamiento solo si el menú está abierto
  animation: ${({ isOpen }) => (isOpen ? slideDown : "none")} 0.5s ease;

  @media (max-width: 1323px) {
    position: absolute;
    top: 70px;
  }
`;

// Opciones del menú móvil
export const OptionsMenuMobile = styled.div`
  color: #dcdedf;
  font-size: 16px;
  font-weight: 550;
  margin: 6.5px 6px;
  cursor: pointer;
  font-family: "Motiva Sans", "Twemoji", "Noto Sans", Helvetica, sans-serif;
  position: relative;
  text-align: center;

  transition: color 0.6s;

  ${({ isSelected }) =>
    isSelected &&
    `
    color: #4b9df8;
    font-weight: bold;
  `}

  &:after {
    content: "";
    display: block;
    height: 2px;
    position: absolute;
    bottom: -5px;
    left: 50%;
    width: 0;
    transition: width 0.3s ease, background-color 0.3s;
  }

  ${({ isSelected }) =>
    isSelected &&
    `
    &:after {
      width: 100%;
      left: 0;
    }
  `}
`;
