import React from "react";
import styled from "styled-components";

const NavbarContainer = styled.nav`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: #1b2838;
  padding: 10px 20px;
  color: #ffffff;
`;

const Logo = styled.div`
  font-size: 1.5rem;
  font-weight: bold;
  cursor: pointer;
  color: #c7d5e0;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 15px;
`;

const NavLink = styled.a`
  color: #c7d5e0;
  text-decoration: none;
  font-size: 0.9rem;
  &:hover {
    color: #ffffff;
    text-decoration: underline;
  }
`;

const UserOptions = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
  font-size: 0.9rem;
`;

const Button = styled.button`
  background-color: #5c7e10;
  color: #ffffff;
  border: none;
  padding: 5px 10px;
  cursor: pointer;
  font-weight: bold;
  &:hover {
    background-color: #4b660c;
  }
`;

const LanguageSelector = styled.div`
  cursor: pointer;
  color: #c7d5e0;
`;

function Navbar() {
  return (
    <NavbarContainer>
      <Logo>STEAM</Logo>
    </NavbarContainer>
  );
}

export default Navbar;
