import styled from "styled-components";

export const NavbarContainer = styled.nav`
  display: flex;
  align-items: flex-start; // Alineación al inicio
  justify-content: space-between;
  background-color: #181d24;
  padding: 30px 279px;
  color: #ffffff;
  padding-top: 10px; // Ajustar el padding superior para mover todo hacia abajo, si es necesario
`;

export const StyledLogo = styled.img`
  width: 176px;
  height: auto;
`;

export const OptionsContainer = styled.div`
  margin-top: 5px;
  margin-left: 2.6%;
  display: flex; // Alineando horizontalmente
  margin-right: auto; // Mover el contenedor a la derecha
`;

export const Option = styled.div`
  color: #dcdedf;
  font-size: 16px;
  font-weight: 550;
  margin: 0 6px; // Espaciado entre opciones
  cursor: pointer; // El cursor se pone en modo click al pasar por él
  font-family: "Motiva Sans", "Twemoji", "Noto Sans", Helvetica, sans-serif;
  &:hover {
    text-decoration: underline; // Como hover usa una línea abajito
  }
`;

export const OptionsContainerLogin = styled.div`
  display: flex; // Alineando horizontalmente
  margin-left: auto; // Mover el contenedor a la derecha
  align-items: flex-start; // Alineación superior para pegarlo a la parte de arriba
  margin-top: -5px; // Usa un margen negativo para mover el "Inicio de sesión" más arriba
`;

export const OptionLogin = styled.div`
  color: #dcdedf;
  font-size: 12px;
  font-weight: 501;
  margin: 0 6px; // Espaciado entre opciones
  cursor: pointer; // El cursor se pone en modo click al pasar por él
  font-family: "Motiva Sans", "Twemoji", "Noto Sans", Helvetica, sans-serif;
`;
