import React, { useState } from "react";
import {
  NavbarContainer,
  StyledLogo,
  OptionsContainer,
  Option,
  OptionsContainerLogin,
  OptionLogin,
  VerticalBar,
  HamburgerMenu,
  HamburgerIcon,
  MobileOptionsContainer,
} from "../styles/NavbarStyles";
import LogoSvg from "../assets/steamLogo.svg"; // Ajusta la ruta según sea necesario

function Navbar() {
  const [selectedOption, setSelectedOption] = useState("TIENDA"); // Estado para la opción seleccionada
  const [isOpen, setIsOpen] = useState(false); // Estado para el menú hamburguesa

  const handleOptionClick = (option) => {
    setSelectedOption(option); // Cambiar la opción seleccionada al hacer clic
    setIsOpen(false); // Cerrar el menú hamburguesa al seleccionar una opción
  };

  return (
    <>
      <NavbarContainer>
        <StyledLogo src={LogoSvg} alt="Logo de Steam" />
        <HamburgerMenu onClick={() => setIsOpen(!isOpen)}>
          <HamburgerIcon isOpen={isOpen} />
        </HamburgerMenu>
        <OptionsContainer
          style={{ display: window.innerWidth > 1323 ? "flex" : "none" }}
        >
          {["TIENDA", "COMUNIDAD", "ACERCA DE", "SOPORTE"].map((option) => (
            <Option
              key={option}
              onClick={() => handleOptionClick(option)}
              isSelected={selectedOption === option} // Verifica si la opción está seleccionada
            >
              {option}
            </Option>
          ))}
        </OptionsContainer>
        <OptionsContainerLogin>
          <OptionLogin>Inicio de sesión</OptionLogin>
          <VerticalBar />
          <OptionLogin>Registrarse</OptionLogin>
        </OptionsContainerLogin>
      </NavbarContainer>
      {isOpen && (
        <MobileOptionsContainer>
          {["TIENDA", "COMUNIDAD", "ACERCA DE", "SOPORTE"].map((option) => (
            <Option
              key={option}
              onClick={() => handleOptionClick(option)}
              isSelected={selectedOption === option} // Verifica si la opción está seleccionada
            >
              {option}
            </Option>
          ))}
        </MobileOptionsContainer>
      )}
    </>
  );
}

export default Navbar;
