import styled from "styled-components";

export const NavbarContainer = styled.nav`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: #181d24;
  padding: 30px 279px;
  color: #ffffff;

  @media (max-width: 1342px) {
    padding: 30px 20px; // Ajuste de padding para pantallas más pequeñas
  }
`;

export const StyledLogo = styled.img`
  width: 176px;
  height: auto;

  @media (max-width: 1323px) {
    width: 100px; // Reducción del tamaño del logo
    margin: 0 auto; // Centrando el logo
  }
`;

export const OptionsContainer = styled.div`
  margin-top: 5px;
  margin-left: 2.6%;
  display: flex; // Alineando horizontalmente
  margin-right: auto; // Mover el contenedor a la derecha

  @media (max-width: 1323px) {
    display: ${(props) =>
      props.isOpen ? "flex" : "none"}; // Ocultar o mostrar opciones
    flex-direction: column; // Cambiar a disposición vertical
    position: absolute;
    top: 70px; // Ajusta la posición según sea necesario
    background-color: #181d24; // Fondo del menú
    width: 100%; // Ancho completo
    padding: 10px 0; // Espaciado interno
  }
`;

export const MobileMenuButton = styled.button`
  display: none; // Ocultar por defecto

  @media (max-width: 1323px) {
    display: block; // Mostrar botón en pantallas pequeñas
    background: none;
    border: none;
    color: #ffffff;
    font-size: 24px;
    cursor: pointer;
  }
`;

export const MobileMenu = styled.div`
  display: none; // Ocultar por defecto

  @media (max-width: 1323px) {
    display: ${(props) =>
      props.isOpen ? "flex" : "none"}; // Mostrar menú si está abierto
    flex-direction: column; // Cambiar a disposición vertical
    background-color: #181d24; // Fondo del menú
    width: 100%; // Ancho completo
    position: absolute;
    top: 70px; // Ajusta según sea necesario
    left: 0;
    padding: 10px 0; // Espaciado interno
  }
`;

export const Option = styled.div`
  color: #dcdedf;
  font-size: 16px;
  font-weight: 550;
  margin: 0 6px; // Espaciado entre opciones
  cursor: pointer; // El cursor se pone en modo click al pasar por él
  font-family: "Motiva Sans", "Twemoji", "Noto Sans", Helvetica, sans-serif;
  position: relative; // Para posicionar la línea de subrayado

  transition: color 0.6s; // Añadir la transición para el cambio de color y la línea

  ${({ isSelected }) =>
    isSelected &&
    `
    color: #4b9df8; // Cambia el color del texto
    font-weight: bold; // Opcional: cambia el peso de la fuente
  `}

  &:after {
    content: "";
    display: block;
    height: 2px; // Altura de la línea de subrayado
    background-color: ${({ isSelected }) =>
      isSelected ? "red" : "transparent"}; // Color de la línea
    position: absolute;
    bottom: -5px; // Posición de la línea debajo del texto
    left: 50%; // Comenzar en el centro
    width: 0; // Inicialmente la línea tiene ancho 0
    transition: width 0.3s ease, background-color 0.3s; // Transición suave para el ancho y el color
  }

  // Cuando está seleccionada, expandimos la línea
  ${({ isSelected }) =>
    isSelected &&
    `
    &:after {
      width: 100%; // Expandir a 100% al estar seleccionada
      left: 0; // Alinear a la izquierda para cubrir toda la opción
    }
  `}
`;

export const OptionsContainerLogin = styled.div`
  margin-top: 5px;
  display: flex; // Alineando horizontalmente
  margin-left: auto; // Mover el contenedor a la derecha
  align-items: center; // Alinear verticalmente al centro
`;

export const OptionLogin = styled.div`
  color: #dcdedf;
  font-size: 12px;
  font-weight: 500;
  margin: 0 6px; // Espaciado entre opciones
  cursor: pointer; // El cursor se pone en modo click al pasar por él
  font-family: "Motiva Sans", "Twemoji", "Noto Sans", Helvetica, sans-serif;
`;

export const VerticalBar = styled.div`
  width: 1px; // Ancho de la barra
  height: 20px; // Altura de la barra
  background-color: #dcdedf; // Color de la barra
  margin: 0 10px; // Espaciado a los lados de la barra
`;
