import React, { useState } from "react";
import {
  NavbarContainer,
  StyledLogo,
  OptionsContainer,
  Option,
  OptionsContainerLogin,
  OptionLogin,
  VerticalBar,
} from "../styles/NavbarStyles";
import LogoSvg from "../assets/steamLogo.svg"; // Ajusta la ruta según sea necesario

function Navbar() {
  const [selectedOption, setSelectedOption] = useState("TIENDA"); // Estado para la opción seleccionada

  const handleOptionClick = (option) => {
    setSelectedOption(option); // Cambiar la opción seleccionada al hacer clic
  };

  return (
    <>
      <NavbarContainer>
        <StyledLogo src={LogoSvg} alt="Logo de Steam" />
        <OptionsContainer>
          {["TIENDA", "COMUNIDAD", "ACERCA DE", "SOPORTE"].map((option) => (
            <Option
              key={option}
              onClick={() => handleOptionClick(option)}
              isSelected={selectedOption === option} // Verifica si la opción está seleccionada
            >
              {option}
            </Option>
          ))}
        </OptionsContainer>
        <OptionsContainerLogin>
          <OptionLogin>Inicio de sesión</OptionLogin>
          <VerticalBar />
          <OptionLogin>Registrarse</OptionLogin>
        </OptionsContainerLogin>
      </NavbarContainer>
    </>
  );
}

export default Navbar;
